import * as vars from './private';

function checkVars(obj: object | string | boolean, key?: string) {
  if (obj !== undefined) {
    if (typeof obj == 'string' || typeof obj == 'boolean') {
      return;
    }
    const keys = Object.keys(obj);
    if (keys.length > 0) {
      Object.keys(obj).forEach((key) => {
        checkVars(obj[key], key);
      });
    }
    return;
  }
  throw new Error('Missing ENV property' + (key ? ': ' + key : ''));
}

checkVars(vars);
