import axios from 'axios';

const post = async (path, data = {}) => {
  const resp = await axios.post(path, data);
  if (!resp.data || resp.data.error) {
    throw resp.data; // TODO: set uiAlert
  }
  return resp.data;
};

// TODO: remove
export const doSomething = (data) => post('/api/do-something', data);
